import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import FoodForm from '../components/FoodForm'
import AdminPanel from '../components/AdminPanel'
import { useDispatch, useSelector } from "react-redux";
import axios from 'axios';




const FormSection = styled.div`
    height: 80vh;
    width: max-content;
    background-color: #3256D4;
    display: flex;
    margin :auto;
    margin-top: 20px;
`



function FoodUpload() {

    const [food,setFood] = useState({});

    const dispatch = useDispatch();
    const ingrendients = useSelector(
    (state) => state.ingrendientsReducer.ingrendients
    );

    const uploadFood = async () =>{
            axios.post("http://localhost:8000/foods/uploadFood/",food)
            .then(response => {
                alert(response.data + " " + "Added in Database")
      })
            .catch(error => {
                console.log(error)
      })   
    }

    useEffect(()=>{
        
        uploadFood()
    },[food])
 
    return (

        <div>
            <AdminPanel/>
            <FormSection>
                <FoodForm setFood={setFood} ingrendients={ingrendients} />
            </FormSection>
        </div>
        
       
    )
}

export default FoodUpload
