import React, { useEffect,useState } from 'react'
import styled from 'styled-components'
import AdminPanel from '../components/adminPanel/AdminPanel'
import FoodsList from '../components/foodsList/FoodsList'
import { Search as SearchIcon} from '@styled-icons/bootstrap/Search'
import {useDispatch,useSelector} from 'react-redux'
import { getFoods } from '../redux/actions/foods'


const SearchbarSection = styled.div`
 height: 50px;
  margin-top: 10px;
  margin-left: 150%;
  grid-template-columns: 3fr 7fr;
  display: grid;
`

const Searchbar = styled.input`
  width:200px ;
  height:20px ;
`
const Search = styled(SearchIcon)`
  width:100px ;
  height:20px ;
`

const ContentSection = styled.div`
    height: 90vh;    
    background-color: #3256D4;
    width: 1000px;    
    position: absolute;    
    margin-left: 20%;
    margin-top: 20px;
    top:20px ;
    overflow-y: scroll;
    
`
const Grid = styled.div`
    display: grid;
    grid-template-columns: 3fr 7fr;
    overflow:hidden;
   
`



function Foods() { 
  
  const dispatch = useDispatch();
  const [search, setSearch] = useState("")
  const [foods,setFoods] = useState([])
  // const foods = useSelector((state) => state.foodsReducer.products)
  const products = useSelector((state) => state.foodsReducer.products)
  

   
  useEffect(()=>{
    dispatch(getFoods())
    setFoods(products)    
  },[products]) 
  
  useEffect(() =>{
    if(search){
      const item =  foods.find(({ title }) => title === search ) 
      setFoods(item) 
      console.log(item)
    }
    
  },[search])

  return (

     
    <Grid>     
      <AdminPanel />
        
                
        <SearchbarSection >               
          <Searchbar onChange={(e) =>setSearch(e.target.value)}/>
          <Search />           
        </SearchbarSection> 

        
        <ContentSection>
          {foods?.length === 0 || foods === undefined ? 
            <div>
            <h4>Loading...</h4>
          </div>
          : foods.map((item) =>{
            return(<FoodsList
                  id={item._id}
                  title={item.title} 
                  img={item.img} 
                  ingrendients={item.ingrendients} 
                  price={item.price} />)
                  
          })
          
          }        
        </ContentSection>  
        
    </Grid>
    
  )
}

export default Foods
