import axios from 'axios'
import React, { useEffect,useState } from 'react'
import {useParams} from 'react-router-dom'
import styled from 'styled-components';
import AdminPanel from '../components/AdminPanel';
import FoodForm from '../components/FoodForm';

import Food from '../components/Food';


const FormSection = styled.div`
    height: 80vh;
    width: max-content;
    background-color: #3256D4;
    display: flex;
    margin :auto;
    margin-top: 20px;
`



function FoodUpdate() {

const {id}  = useParams();
const [food,setFood] = useState({});
const [buttonClicked,setButtonClicked] = useState(false)
const [isLoaded, setIsLoaded] = useState(false)
const [ingrendients,setIngrendients] = useState([])

const fetchData = async () =>{
    const response = await axios
    .get(`http://localhost:8000/foods/${id}`)
    .catch((err) =>{
      console.log(err);     
    });
    setFood(response.data) 
    setIngrendients(response.data.ingrendients)
        
}  


useEffect(() =>{     
     fetchData()
     setIsLoaded(true) 
     console.log(ingrendients)
     console.log(food.title)  
     console.log(food.price) 
     console.log(food.img) 
     console.log(food.category)    
},[fetchData()])

  return (
    <div>
        <AdminPanel/>
        <FormSection>
          {/* { isLoaded === true ?
               <FoodForm 
                isLoaded={isLoaded}
                food={food}
                setFood={setFood}
                setButtonClicked={setButtonClicked}
              />:
              <div>
               <h2>...Loading</h2> 
              </div>
          } */}
         
         

         
        </FormSection>
    </div>
    
  )
}

export default FoodUpdate
