import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Food from "./Food";
import { addIngrendient } from "../redux/actions/Actions";
import { useDispatch, useSelector } from "react-redux";

const Form = styled.div`
  display: flex;
  margin: auto;
  flex-direction: column;
  width: 50%;
  margin-top: 10vh;
`;

const InputBox = styled.input`
  height: 40px;
  width: max-content;
`;
const UploadButton = styled.button`
  height: 40px;
  width: max-content;
`;
const AddButton = styled.button`
  height: 20px;
  width: min-content;
`;

const Col2 = styled.div``;

const PreviewSection = styled.div`
  height: max-content;
  width: max-content;
`;

function FoodForm({ setFood }) {
  const dispatch = useDispatch();
  const ingrendients = useSelector(
    (state) => state.ingrendientsReducer.ingrendients
  );

  useEffect(() => {
    console.log(ingrendients);
  }, [ingrendients]);

  const [title, setTitle] = useState("");
  const [imgUrl, setImgUrl] = useState("");
  const [ingrendient, setIngrendient] = useState("");
  const [price, setPrice] = useState("");

  const product = {
    title: title,
    img: imgUrl,
    ingrendients: ingrendients,
    price: price,
  };

  const pushIngrendient = (e) => {
    dispatch(addIngrendient(ingrendient));
    e.preventDefault();
  };

  return (
    <Form>
      <h1>Upload food</h1>
      <InputBox
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      ></InputBox>

      <InputBox
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      ></InputBox>

      <InputBox
        placeholder="Image URL"
        value={imgUrl}
        onChange={(e) => setImgUrl(e.target.value)}
      ></InputBox>

      <Col2>
        <InputBox
          placeholder="Ingredients"
          value={ingrendient}
          onChange={(e) => setIngrendient(e.target.value)}
        ></InputBox>

        <AddButton onClick={pushIngrendient}>Add</AddButton>
      </Col2>
      <UploadButton onClick={(e) => setFood(product)}>Upload it</UploadButton>

      <PreviewSection>
        <Food
          title={product.title}
          img={product.img}
          ingrendients={product.ingrendients}
          price={product.price}
        />
      </PreviewSection>
    </Form>
  );
}

export default FoodForm;
