import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import FoodForm from '../components/FoodForm'
import AdminPanel from '../components/AdminPanel'
import { useDispatch, useSelector } from "react-redux";
import { resetIngrendients } from '../redux/actions/Actions';
import axios from 'axios';




const FormSection = styled.div`
    height: 80vh;
    width: max-content;
    background-color: #3256D4;
    display: flex;
    margin :auto;
    margin-top: 20px;
`



function FoodUpload() {

    const [food,setFood] = useState({});
    const [buttonClicked,setButtonClicked] = useState(false);

    const dispatch = useDispatch();
    const ingrendients = useSelector(
    (state) => state.ingrendientsReducer.ingrendients
    );

    const uploadFood =  () =>{
            axios.post("http://localhost:8000/foods/uploadFood/",food)
            .then(response => {
                alert(response.data + " " + "Added in Database")
      })
            .catch(error => {
                console.log(error)
      })   
    }

    useEffect(()=>{
        if(buttonClicked === true){
            uploadFood()
        }


        
    },[food,buttonClicked])
 
    return (

        <div>
            <AdminPanel/>
            <FormSection>
                <FoodForm food={food} buttonClicked={setButtonClicked} setFood={setFood} ingrendients={ingrendients} />
            </FormSection>
        </div>
        
       
    )
}

export default FoodUpload
