import { FETCH_FOODS } from "../constants/actionTypes"

const initialState = {
  products: [],
}

export const foodsReducer = (state = initialState, {type,payload}) => {
  switch (type) {
    case FETCH_FOODS:        
      return { ...state,products:[ ...state.products,payload] } 
      
    default:
      return state
  }
};
