import {ADD_FOODS} from '../constants/actionTypes'
import * as api from '../../api/index'

export const getFoods = () => async (dispatch) =>{
    try{
        const  {data}  = await api.fetchFoods()
        dispatch({ type:ADD_FOODS, payload:data })
    }catch(error){
        console.log(error)
    }
}
