import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Food from "./Food";
import { addIngrendient } from "../redux/actions/Actions";
import { useDispatch, useSelector } from "react-redux";


const Form = styled.div`
  display: flex;
  margin: auto;
  flex-direction: column;
  width: 50%;
  margin-top: 10vh;
`;

const InputBox = styled.input`
  height: 40px;
  width: max-content;
`;
const UploadButton = styled.button`
  height: 40px;
  width: 100px;
  background-color:#32CD32 ;
  border-radius:5px;
`;
const AddButton = styled.button`
  height: 20px;
  width: min-content;
`;

const Col2 = styled.div``;

const PreviewSection = styled.div`
  height: max-content;
  width: max-content;
`;

function FoodForm(props) {
  const dispatch = useDispatch();

  const [title, setTitle] = useState("");
  const [imgUrl, setImgUrl] = useState("");
  const [ingrendient, setIngrendient] = useState("");
  const [category,setCategory] = useState("")
  const [price, setPrice] = useState("");
  const [ingrendients,setIngrendients] = useState([]);

  const product = {
    title: title,
    img: imgUrl,
    ingrendients: props.ingrendients,     
    price: price,
    category:category
  };

  const passData = () => {
    const food = props.food
    
    setTitle(food.title)
    setImgUrl(food.img)
    setIngrendients(food.ingrendients)
    setPrice(food.price)
    setCategory(food.category)
    setIngrendients(food.ingrendients)
    product.ingrendients = food.ingrendients

    
  }

  useEffect(()=>{

    if(props.isLoaded === true){
      passData()
    }
    console.log(product)
  },[])

  const pushIngrendient = (e) => {
    dispatch(addIngrendient(ingrendient));
     setIngrendient("");
    
  };

  const setProduct = (product) =>{
      if( product.ingrendients.length === 0 
        || product.category  === "" 
        || product.title  === "" 
        || product.price  === "" 
        || product.img  === "" ){
        alert("Fill all textboxes")
      }else{   
      props.setFood(product)
      props.buttonClicked(true);    
     }
  } 

  return (
    <Form>
      <h1>Upload Food / Update Food</h1>
      <InputBox
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      ></InputBox>

      <InputBox
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      ></InputBox>

      <InputBox
        placeholder="Image URL"
        value={imgUrl}
        onChange={(e) => setImgUrl(e.target.value)}
      ></InputBox>

       

      <Col2>
        <InputBox
          placeholder="Ingredients"
          value={ingrendient}
          onChange={(e) => setIngrendient(e.target.value)}
          
        ></InputBox>

        <AddButton onClick={pushIngrendient}>Add</AddButton>
      </Col2>
      <select className="category-select" onChange={(e)=>{
           setCategory(e.target.value)
       }} >
            <option value="">Select Category</option>
            <option value="meat">Meat</option>
            <option value="pizza">Pizza</option>
            <option value="soup">Soup</option>
            <option value="dessert">Dessert</option>
            <option value="coffee">Coffee</option>
       </select>
      <UploadButton onClick={(e) => setProduct(product)}>Save</UploadButton>

      <PreviewSection>
        <Food
          title={product.title}
          img={product.img}
          // ingrendients={product.ingrendients}
          price={product.price}
          category={product.category}
        />
      </PreviewSection>
    </Form>
  );
}

export default FoodForm;
