import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import FoodForm from '../components/FoodForm'
import { useSelector} from "react-redux";
import AdminPanel from '../components/AdminPanel';
import axios from 'axios';





const FormSection = styled.div`
    height: 80vh;
    width: max-content;
    background-color: #3d5a80;
    display: flex;
    margin :auto;
    margin-top: 20px;
`

const PreviewSection = styled.div`
    height: 100vh;
    width: max-content;
`


function FoodUpload() {

    const [food, setFood] = useState({})
    const [isClicked,setIsClicked] = useState(false);
    const ingrendients = useSelector(
        (state) => state.ingrendientsReducer.ingrendients
      );
    const postFood = async () =>{
        const response = await axios.post("http://localhost:8000/foods/upload",food) 
            .catch((err) =>{
                console.log(err)
            })

            alert(response.data)
    }

    useEffect(() =>{
        if(isClicked){
         postFood()
         
        }
    },[isClicked])  

    return (
        <div>
        <AdminPanel />
            <FormSection>
                <FoodForm setFood={setFood} ingrendients={ingrendients} setIsClicked={setIsClicked} ></FoodForm>
            </FormSection>
            
        
        </div>
    )
}

export default FoodUpload
