import React,{useState} from 'react'
import {Container,Wrap,DropDown,Grid} from './style'

const Accordion = ({products,total,message}) => {
    const [isClicked, setIsClicked] = useState(false)

    const toggle = () =>{
      setIsClicked(!isClicked)
    }

  return (
    <Container>
      <Grid>
        <div>
          <h3>{products.length} {products.length >1 ? ("products") : ("product")}</h3>
        </div>
        <div>
          <h3>{total}$</h3>
        </div>
        <div>
          <h3 id='toggle' onClick={toggle}>{isClicked ? ("-") : ("+")}</h3>
        </div>
      </Grid>
      {isClicked ? 
      (
      <DropDown>
        <hr/>
        {products.map(item =>{
          return(<div>
            <h3>{item.title} quantity: x{item.quantity}</h3>
            {item.ingrendients.map(item =>{
              return(<ul><li>{item}</li></ul>)
            })}
          </div>)
        })}
        
        <h4>Comments:</h4>
        <p>''{message}''</p>
      </DropDown>) :
       (null)}

    </Container>
  )
}

export default Accordion
