import { FETCH_FOODS } from "../constants/actionTypes"

export const foodsReducer = (state = { products: [] }, {action,payload}) => {
  switch (action.type) {
    case FETCH_FOODS:        
      return {  products: payload.data } 
      
    default:
      return state;
  }
};
