import styled from "styled-components";
import React, {useState} from 'react';
import Basket from "./Basket";
import {Basket2,List}  from "@styled-icons/bootstrap";


export const HeaderDiv = styled.div `
height: 30px;
width: 100%;
display: flex;
margin-bottom: 50px;
display: grid;
grid-template-columns: 0.5fr 3fr 0.5fr;
    
`


export const SearchBar = styled.input`
    font-family: -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Oxygen,Ubuntu,Cantarell,'Open Sans','Helvetica Neue',sans-serif;    font-size: 16px;
    border: none;
    font-style: italic;
    text-align: center;
    font-weight: 300;
    width: 400px;
    height: 25px;
    margin-left: auto;
    margin-right: auto;
    
`
export const MenuButton = styled.button`
    
    width: 50px;
    height: 45px;
    border: 2px solid;
    border-radius: 3px; 
    background-color: #d8a28c;
`

export const BasketButton = styled.button`
    width: 50px;
    margin-left: auto;
    height: 45px;
    border: 2px solid;
    border-radius: 3px; 
    background-color: #d8a28c;
`

export const Orders = styled.div`
     height: 15px;
  width: 15px;
  background-color: red;
  border-radius: 50%;
  margin-top: -60px;
  margin-left: 30px;
`
    


export const Quantity = styled.h5`
    color: #ffff;
`







export const Header = () =>{

    const [orders, setOrders] = useState(0);

    const basketButton = (e) => {
        return( <Basket>
            </Basket>) 
    }

    return(
        <HeaderDiv>
            <MenuButton > <List/> </MenuButton>  
            
            <SearchBar placeholder="Search" />             
            <BasketButton onClick={basketButton} >  <Basket2/> <Orders><Quantity>{orders}</Quantity></Orders>  </BasketButton>
        </HeaderDiv>
    );
}

export default Header;
