import styled from "styled-components";
import React from 'react';
import {Basket,Cart} from 'styled-icons/bootstrap'

export const HeaderDiv = styled.div `
height: 30px;
width: 100%;
display: flex;
margin-bottom: 50px;
display: grid;
grid-template-columns: 0.5fr 3fr 0.5fr;
    
`


export const SearchBar = styled.input`
    font-family: -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Oxygen,Ubuntu,Cantarell,'Open Sans','Helvetica Neue',sans-serif;    font-size: 16px;
    border: none;
    font-style: italic;
    text-align: center;
    font-weight: 300;
    width: 400px;
    height: 25px;
    margin-left: auto;
    margin-right: auto;
    
`
export const MenuButton = styled.button`
    width: 50px;
`

export const CartButton = styled.button`
    width: 50px;
    margin-left: auto;
    height: 25px;
    border: 2px solid;
    border-radius: 3px; 
    background-color: #d8a28c;
`

export const BasketIcon = styled.i`
    width: 50px;
    height: 25px;
    border: 2px solid;
    border-radius: 3px; 
    background-color: #d8a28c;
`

export const CartIcon = styled.i`

`



export const Header = () =>{
    return(
        <HeaderDiv>
            <MenuButton ><BasketIcon ><Basket/></BasketIcon> </MenuButton>  
            
            <SearchBar placeholder="Search" />             
            <CartButton > <CartIcon> <Cart /> </CartIcon> </CartButton>
        </HeaderDiv>
    );
}

export default Header;
