import React from "react";
import { OrderItem,OrdersContainer,DescriptionContainer,TrashIcon,FoodTitle,Quantity,DeleteButton,Price } from "./styles";
import { useSelector,useDispatch } from "react-redux";
import { deleteCart } from "../../redux/actions/Actions";



function Basket() {
  const products = useSelector((state) => state.orderReducer.products);
  const total = useSelector((state) => state.orderReducer.total);
 

  const dispatch = useDispatch();

  const deleteItem = (id) => {
    dispatch(deleteCart(id));    
  };

 
  

  return (
    <OrdersContainer>
      <DescriptionContainer>
        <h3>Product</h3>
        <h3>Quantity</h3>
        <h3>Price</h3>
        <></>
      </DescriptionContainer>
      { products === 0 ? (
        <div>
          <h4>No Products</h4>
        </div>
      ) : (
        products.map((product, i) => {
          return (
            <OrderItem>
              <FoodTitle>{product.title}</FoodTitle>
              <Quantity value={product.quantity}/>              
              <Price>{product.price}$</Price>
              <DeleteButton>
                <TrashIcon onClick={() => deleteItem(product.id)} />
              </DeleteButton>
              <div>
                {product.ingrendients.map((item,i) =>{
                  return(<li>{item}</li>)
                })}
              </div>
            </OrderItem>
          );
        })
      )}
    </OrdersContainer>
  );
}

export default Basket;
