import { ADD_CART,REMOVE_CART } from "../constants/ActionTypes";



  export const orderReducer = (state = {products:[],total:0}, { type, payload }) => {

    
    
    switch (type) {
      case ADD_CART:

         const price = Number(payload.price)*Number(payload.quantity)        
         const product = {id:payload.id,title:payload.title,quantity:payload.quantity,price:price,ingrendients:payload.ingrendients}
         const subTotal = state.total+price
        return {...state,products:[...state.products,product],total:subTotal}; 
         
        
      case REMOVE_CART:
             let clone = state.products;
             let productToDelete = state.products.find((item) => item.id === payload)
             
             let tempSubTotal = state.total-productToDelete.price           

             clone.splice(clone.indexOf(productToDelete),1);
             return {...state,products:clone,total:tempSubTotal};

    
      default:
        return state;
    }
  };
