import styled from "styled-components";
import React, {useState} from 'react';
import Basket from "./Basket";
import {House}  from "@styled-icons/bootstrap";
import {Basket3 as Basket2}  from "@styled-icons/bootstrap";


export const HeaderDiv = styled.div `
height: 30px;
width: 100%;
display: flex;
margin-bottom: 50px;
display: grid;
grid-template-columns: 0.5fr 3fr 0.5fr;
    
`


export const SearchBar = styled.input`
    font-family: -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Oxygen,Ubuntu,Cantarell,'Open Sans','Helvetica Neue',sans-serif;    font-size: 16px;
    border: none;
    font-style: italic;
    text-align: center;
    font-weight: 300;
    width: 400px;
    height: 25px;
    margin-left: auto;
    margin-right: auto;
    
`
export const MenuButton = styled.button`
    
    width: 50px;
    height: 45px;    
    border-radius: 3px; 
    background-color: #d8a28c;
    cursor: pointer;
`

export const BasketButton = styled.div`
    width: 50px;
    margin-left: auto;
    height: 45px;   
    border-radius: 3px; 
    background-color: #d8a28c;
    cursor: pointer;
`
export const BasketIcon = styled(Basket2)`
    width: 30px;
    height: 30px;
    margin-left: 10px;
    margin-top: 8px;
`

export const HomeIcon = styled(House)`
    width: 30px;
    height: 30px;
`

export const Orders = styled.div`
  height: 15px;
  width: 15px; 
  background-color: red;
  border-radius: 50%;
  margin-top: -60px;
  margin-left: 35px;
`
    


export const Quantity = styled.h5`
    color: #ffff;
`







export const Header = () =>{

    const [orders, setOrders] = useState(0);

    const basketButton = (e) => {
        setOrders(orders+1);
        console.log(orders);
    }

    return(
        <HeaderDiv>
            <MenuButton > <House/> </MenuButton>  
            
            <SearchBar placeholder="Search" />             
            <BasketButton onClick={basketButton}  >  <BasketIcon /> <Orders><Quantity>{orders}</Quantity></Orders>  </BasketButton>
        </HeaderDiv>
    );
}

export default Header;
