import { ADD_CART,REMOVE_CART } from "../constants/ActionTypes";

const initialState = {
    products: {}
  };

  export const productReducer = (state =initialState, { type, payload }) => {
    switch (type) {
      case ADD_CART:

        return [...state,{
          id:payload.id,
          title:payload.title,
          quantity:payload.quantity
        }];      
      default:
        return state;
    }
  };
