import { ADD_CART,REMOVE_CART } from "../constants/ActionTypes";

const initialState = {
    products: [{id:"1",title: 'Pancake', img: 'https://images.pexels.com/photos/7144893/pexels-photo-7144893.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940',ingrendients:['salt','peper','oil']}]
  };

  export const productReducer = (state =initialState, { type, payload }) => {
    switch (type) {
      case ADD_CART:
         return { ...state, products: payload };      
        
      default:
        return state;
    }
  };
