import React,{useState} from "react";
import { Grid,OrdersContainer,DescriptionContainer,TrashIcon,Quantity,DeleteButton,Ingrendients,Item,OrdersList, Footer } from "./styles";
import { useSelector,useDispatch } from "react-redux";
import { deleteCart,deleteIngrendient, updateQuantity } from "../../redux/actions/Actions";





function Basket() {
  const products = useSelector((state) => state.orderReducer.products);
  const total = useSelector((state) => state.orderReducer.total);
 const [quantity, setQuantity] = useState("")

  const dispatch = useDispatch();

  const deleteItem = (id) => {
    dispatch(deleteCart(id));    
  };

  const deleteIng = (data) =>{
    dispatch(deleteIngrendient(data))
  }

  const changeQuantity = (data) =>{
    if(data.eve.keyCode === 13){
      if(quantity !== ""){
        dispatch(updateQuantity({id:data.id,quantity:quantity}))
        setQuantity("")
      }else{
        alert("Write Quantity")
      }
      
      
    }
    
  }

 
  

  return (
    <OrdersContainer>
      <DescriptionContainer>
        <h3>Product</h3>
        <h3>Quantity</h3>
        <h3>Price</h3>
        <h3>SubTotal</h3>
        <></>
      </DescriptionContainer>
      <OrdersList>
        { products === 0 ? (
          <div>
            <h4>No Products</h4>
          </div>
        ) : (
          products.map((product, i) => {
            return (
              <Item>
                <div>
                  <Grid>
                    <h3>{product.title}</h3>
                    <Quantity placeholder={product.quantity}  onChange={(e) =>{setQuantity(e.target.value)}} onKeyDown={(e) => {changeQuantity({eve:e,id:product.id})}}/>              
                    <h3>{product.price}$</h3>
                    <h3>{product.subTotal}$</h3>
                    <DeleteButton>
                      <TrashIcon onClick={() => deleteItem(product.id)} />
                    </DeleteButton>             
                  </Grid>
                </div>
                  <div>
                    {product.ingrendients.map((item,i) =>{
                      return(<Ingrendients><li>{item}</li> <button onClick={(e) =>{deleteIng({id:product.id,item:item})}}>-</button></Ingrendients>)
                    })}
                  </div>
              </Item>
            );
          })
        )}
      </OrdersList>  
      <Footer>
        <div>
          <textarea>Write a message</textarea>
        </div>
        <h2>Total:{total}</h2>
        
      </Footer>
    </OrdersContainer>
  );
}

export default Basket;
